import React, { useState } from "react";
import "./auth.css";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faKey, faPlus } from "@fortawesome/free-solid-svg-icons";
import Button from "../../components/general/Button";

const Register = ({ setIsLoggedIn, setUserUsername }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const [isSignUp, setIsSignUp] = useState(false); // Track Sign In / Sign Up mode

    const handleSubmit = (e) => {
        e.preventDefault();
        const url = isSignUp 
            ? "http://localhost:8000/api/auth/register" 
            : "http://localhost:8000/api/auth/login";

        axios.post(url, { username, password })
            .then((response) => {
                if (response.data.accessToken) {
                    localStorage.setItem("accessToken", response.data.accessToken);
                    localStorage.setItem("username", username);
                    setUserUsername(username);
                    setIsLoggedIn(true);
                } else {
                    setErrorMessage("Authentication failed.");
                }
            })
            .catch((error) => {
                console.error("Authentication failed:", error);
            });
    };

    return (
        <form className="auth-form" onSubmit={handleSubmit}>
            <label className="input-label">
                <FontAwesomeIcon icon={faUser} className="input-icon" />
                Username:
            </label>
            <div className="input-group">
                <input
                    className="auth-input"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
            </div>
            <label className="input-label">
                <FontAwesomeIcon icon={faKey} className="input-icon" />
                Password:
            </label>
            <div className="input-group">
                <input
                    className="auth-input"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}

            {/* Sign In / Register Button (Icon Switches) */}
            <Button
                label={isSignUp ? "Sign Up" : "Sign In"}
                icon={isSignUp ? faPlus : faKey} // Toggle icon
                className="auth-button"
                type="submit"
                onClick={() => setIsSignUp(!isSignUp)} // Toggle Sign In / Sign Up mode
            />
        </form>
    );
};

export default Register;
