import React, { useState } from "react";
import "./auth.css";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faKey, faPlus } from "@fortawesome/free-solid-svg-icons";
import Button from "../../components/general/Button";

const Register = ({ setIsLoggedIn, setUserUsername }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const [isSignUp, setIsSignUp] = useState(false); // State to track if Sign Up mode is active

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post("http://localhost:8000/api/auth/register", { username, password })
            .then((response) => {
                if (response.data.accessToken) {
                    localStorage.setItem("accessToken", response.data.accessToken);
                    localStorage.setItem("username", username);
                    setUserUsername(username);
                    setIsLoggedIn(true);
                } else {
                    setErrorMessage("Registration failed.");
                }
            })
            .catch((error) => {
                console.error("Registration failed:", error);
            });
    };

    // Toggle between Sign In and Sign Up modes
    const toggleMode = () => {
        setIsSignUp((prevMode) => !prevMode);
    };

    return (
        <form className="auth-form" onSubmit={handleSubmit}>
            <label className="input-label">
                <FontAwesomeIcon icon={faUser} className="input-icon" />
                Username:
            </label>
            <div className="input-group">
                <input
                    className="auth-input"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
            </div>
            <label className="input-label">
                <FontAwesomeIcon icon={faKey} className="input-icon" />
                Password:
            </label>
            <div className="input-group">
                <input
                    className="auth-input"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}

            {/* Button with dynamic icon */}
            <Button
                label={isSignUp ? "Sign Up" : "Sign In"}
                icon={isSignUp ? faPlus : faKey} // Change icon based on mode
                className="auth-button"
                type="submit"
            />

            {/* Toggle button */}
            <button type="button" className="toggle-button" onClick={toggleMode}>
                {isSignUp ? "Switch to Sign In" : ""}
            </button>
        </form>
    );
};

export default Register;
