import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';  // Importing necessary components
import Header from '../../components/Header';  // Adjust the path if necessary
import './dashboard.css';

const Dashboard = ({ userUsername, setIsLoggedIn }) => {
  return (
    <BrowserRouter>
      <div>
        <Header userUsername={userUsername} setIsLoggedIn={setIsLoggedIn} />

        <Routes>
          {/* Route to HomePage component */}
          <Route path="/home" element={<HomePage />} />

          {/* Route to Favorites component */}
          <Route path="/favorites" element={<Favorites />} />

          {/* Route to WatchLater component */}
          <Route path="/watchlater" element={<WatchLater />} />

          {/* Fallback route that redirects to /home */}
          <Route path="*" element={<Navigate to="/home" />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
};

export default Dashboard;
