import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Activity from '../Activity';  // Make sure the path is correct
import './navigation.css';

const SideBar = () => {
  // Define the selected state and its setter function
  const [selected, setSelected] = useState('home');  // This defines both selected and setSelected
  const [activities, setActivities] = useState([]);
  const [showActivities, setShowActivities] = useState(false);

  // Set page function to navigate and set the selected page
  const setPage = (pageName) => {
    setSelected(pageName);  // Now setSelected is properly defined
    // Navigate to the corresponding page (e.g., use navigate() from react-router)
    console.log(`Navigating to ${pageName}`);
  };

  // Fetch activities on component mount
  useEffect(() => {
    axios.get('/api/activity')
      .then((response) => {
        setActivities(response.data);
        setShowActivities(true);
      })
      .catch((error) => {
        console.error("There was an error fetching the activities:", error);
      });
  }, []);

  return (
    <nav>
      <ul>
        <li
          className={selected === 'home' ? 'active' : ''}
          onClick={() => setPage('home')}
        >
          Home
        </li>
        <li
          className={selected === 'favorites' ? 'active' : ''}
          onClick={() => setPage('favorites')}
        >
          Favorites
        </li>
        <li
          className={selected === 'watchlater' ? 'active' : ''}
          onClick={() => setPage('watchlater')}
        >
          Watch Later
        </li>
      </ul>
      <ul>
        {showActivities && activities.slice(0, 10).map((activity, index) => (
          <Activity key={index} activity={activity} />
        ))}
      </ul>
    </nav>
  );
};

export default SideBar;
