import React from 'react';
import './general.css'; // Import the general CSS
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Ensure this import is present

const Input = ({ label, type, className, value, setValue, icon, inputAttributes }) => {
    const handleInput = (event) => {
        setValue(event.target.value);
    };

    return (
        <div className={`input-container ${className}`}>
            {label && <label>{label}</label>}
            <div className="input-wrapper">
                {icon && <span className="input-icon">{icon}</span>} {/* Ensure icon is passed correctly */}
                <input
                    type={type}
                    value={value}
                    onChange={handleInput}
                    {...inputAttributes} // Spread other input attributes
                />
            </div>
        </div>
    );
};

export default Input;
