import React, { useState } from "react";
import "./auth.css";
import Login from "./Login";
import Register from "./Register";
import Button from "../../components/general/Button";
import axios from "axios"; // Import axios
import { faKey, faPlus } from "@fortawesome/free-solid-svg-icons"; // Import icons

const Authentication = ({ setIsLoggedIn, setUserUsername }) => {
    const [_switch, setSwitch] = useState(true); // true for Sign In, false for Sign Up
    const [username, setUsername] = useState(""); // State for username
    const [password, setPassword] = useState(""); // State for password
    const [errorMessage, setErrorMessage] = useState(""); // State for error messages

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior

        const url = _switch ? "/api/auth/login" : "/api/auth/register"; // Determine the URL based on _switch
        const data = { username, password }; // Prepare the data to send

        // Send the POST request
        axios.post(url, data)
            .then((response) => {
                // On success, handle the response
                if (response.data.accessToken) {
                    localStorage.setItem("accessToken", response.data.accessToken); // Store the token
                    localStorage.setItem("username", username); // Store the username
                    setUserUsername(username); // Set the username in state
                    setIsLoggedIn(true); // Set logged in state to true
                } else {
                    setErrorMessage("Invalid response, missing token."); // Handle missing token
                }
            })
            .catch((error) => {
                console.error("Authentication failed:", error);
                setErrorMessage("Authentication failed. Please try again."); // Handle errors
            });
    };

    return (
        <div className="auth-container">
            <div className="auth-box">
                <div className="auth-tabs">
                    <Button
                        label="Sign In"
                        className={_switch ? "active" : ""}
                        onClick={() => setSwitch(true)}
                        icon={faKey}
                    />
                    <Button
                        label="Sign Up"
                        className={!_switch ? "active" : ""}
                        onClick={() => setSwitch(false)}
                        icon={faPlus}
                    />
                </div>
                <h2 className="auth-title">
                    {_switch ? "Sign in with your" : "Create a new"} account
                </h2>
                <form className="auth-form" onSubmit={handleSubmit}> {/* Bind handleSubmit to form */}
                    <label className="input-label">
                        Username:
                    </label>
                    <div className="input-group">
                        <input
                            className="auth-input"
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                    </div>
                    <label className="input-label">
                        Password:
                    </label>
                    <div className="input-group">
                        <input
                            className="auth-input"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Display error message */}
                    <Button
                        label={_switch ? "Sign In" : "Sign Up"}
                        icon={_switch ? faKey : faPlus}
                        className="auth-button"
                        type="submit" // Submit button
                    />
                </form>
            </div>
        </div>
    );
};

export default Authentication;
