// src/components/navigation/SideBar.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './navigation.css';
import Activity from '../Activity';

const SideBar = () => {
  const [selected, setSelected] = useState('home');
  const [small, setSmall] = useState(true);
  const [activities, setActivities] = useState([]);
  const [showActivities, setShowActivities] = useState(false);

  const navigate = useNavigate();

  const setPage = (pageName) => {
    setSelected(pageName);
    navigate(`/${pageName.toLowerCase()}`);
  };

  useEffect(() => {
    // Send GET request to /api/activity to get the activities
    axios.get('/api/activity')
      .then(response => {
        setActivities(response.data); // Assuming response is an array of activities
      })
      .catch(error => {
        console.error('Error fetching activities:', error);
      });
  }, []);

  return (
    <nav className="sidebar">
      <ul>
        <li
          className={selected === 'home' ? 'selected' : ''}
          onClick={() => setPage('Home')}
        >
          Home
        </li>
        <li
          className={selected === 'favorites' ? 'selected' : ''}
          onClick={() => setPage('Favorites')}
        >
          Favorites
        </li>
        <li
          className={selected === 'watchlater' ? 'selected' : ''}
          onClick={() => setPage('Watch Later')}
        >
          Watch Later
        </li>
      </ul>

      {showActivities && (
        <ul>
          {activities.slice(0, 10).map((activity, index) => (
            <Activity key={index} activity={activity} />
          ))}
        </ul>
      )}
    </nav>
  );
};

export default SideBar;
