import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios'; // Import Axios for making HTTP requests
import Dashboard from './components/Dashboard'; // Import Dashboard component
import Authentication from './components/Authentication'; // Import Authentication component

const App = () => {
    // State variables
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userUsername, setUserUsername] = useState("");

    // useEffect to check authentication on mount
    useEffect(() => {
        const accessToken = localStorage.getItem('accessToken'); // Get access token from localStorage

        if (accessToken) {
            // Send POST request to authenticate
            axios.post('/api/auth/', {}, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })
            .then(response => {
                // On success, update state
                setIsLoggedIn(true);
                setUserUsername(response.data.username); // Assuming the response contains a username
            })
            .catch(error => {
                console.error("Authentication error:", error);
                // Handle error (e.g., set isLoggedIn to false)
            });
        }
    }, []); // Empty dependency array means this runs once on mount

    // Conditional rendering based on isLoggedIn state
    return (
        <div className="App">
            {isLoggedIn ? (
                <Dashboard username={userUsername} /> // Pass username to Dashboard
            ) : (
                <Authentication />
            )}
        </div>
    );
};

export default App;
