import React from 'react';
import './general.css'; // Import the general CSS
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Keep this import if you use the icon

const Button = ({ label, className, onClick, icon }) => {
    return (
        <button className={`button ${className}`} onClick={onClick}>
            {icon && <FontAwesomeIcon icon={icon} />} {/* Use the FontAwesomeIcon here */}
            {label}
        </button>
    );
};

export default Button;
