import React, { useState, useEffect } from 'react';
import './App.css'; 
import Dashboard from './components/Dashboard';
import Authentication from './components/Authentication';
import axios from 'axios';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userUsername, setUserUsername] = useState("");

    useEffect(() => {
        const accessToken = localStorage.getItem('accessToken');

        if (accessToken) {
            // Send a POST request to /api/auth/
            axios.post('/api/auth/', {}, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })
            .then(response => {
                // On success, set the state
                setIsLoggedIn(true);
                setUserUsername(response.data.username); 
            })
            .catch(error => {
                console.error("There was an error verifying the token!", error);
            });
        }
    }, []); // Empty dependency array means this runs once when the component mounts

    return (
        <div className="App">
            {isLoggedIn ? (
                <Dashboard username={userUsername} /> // Render the Dashboard component if logged in
            ) : (
                <Authentication /> // Render the Authentication component if not logged in
            )}
        </div>
    );
};

export default App;
